// App.js
import React, { useState, useEffect } from 'react';
import './App.css';
import { trainNetwork, predictPrice } from './neuralNetwork';
import PropertyInputForm from './components/PropertyInputForm';
import PredictedPrice from './components/PredictedPrice';

const App = () => {
    const [predictedPrice, setPredictedPrice] = useState(null);
    // const [propertyInput, setPropertyInput] = useState({
    //     Area: "2500",
    //     Bedrooms: "3",
    //     Bathrooms: "2",
    //     Location: "Suburban",
    //     "Age of Property": "15"
    // });

    useEffect(() => {
        trainNetwork((stats) => {
            console.log(`Training: ${stats.iterations} iterations`);
        });
    }, []);

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setPropertyInput(prevInput => ({ ...prevInput, [name]: value }));
    };

    const handlePredictPrice = () => {
        const predictedPriceValue = predictPrice(propertyInput);
        setPredictedPrice(predictedPriceValue);
    };

    return (
        <div className="App">
            <h1>Real Estate Price Predictor</h1>
            <PropertyInputForm 
                propertyInput={propertyInput} 
                handleInputChange={handleInputChange} 
            />
            <button onClick={handlePredictPrice}>Predict Price</button>
            <PredictedPrice predictedPrice={predictedPrice} />
        </div>
    );
};

export default App;
