import React, { useState } from 'react';
import '../styles/formStyles.css';

const PropertyInputForm = ({ propertyInput, handleInputChange, handlePredictPrice, predictedPrice }) => {
//   const [errors, setErrors] = useState({});
//     const validateForm = () => {
//       const newErrors = {};

//       if (!propertyInput.Area || propertyInput.Area <= 0) {
//         newErrors.Area = 'Area must be a positive number';
//       }

//       if (!propertyInput.Bedrooms || propertyInput.Bedrooms <= 0) {
//         newErrors.Bedrooms = 'Number of bedrooms must be a positive number';
//       }

//       if (!propertyInput.Bathrooms || propertyInput.Bathrooms <= 0) {
//         newErrors.Bathrooms = 'Number of bathrooms must be a positive number';
//       }

//       if (!propertyInput['Age of Property'] || propertyInput['Age of Property'] < 0) {
//         newErrors['Age of Property'] = 'Age of property cannot be negative';
//       }

//       if (!propertyInput.Location) {
//         newErrors.Location = 'Location is required';
//       }

//       setErrors(newErrors);
//       return Object.keys(newErrors).length === 0;
//     };

//     const handleSubmit = (e) => {
//       e.preventDefault();
//       if (validateForm()) {
//         handlePredictPrice();
//       }
//     };

    return (
        <div className="form-container">
            <form className="form" onSubmit={handleSubmit}>
                <div className="input-group">
                    <label>Area:</label>
                    <input 
                        type="number" 
                        name="Area" 
                        value={propertyInput.Area} 
                        onChange={handleInputChange} 
                        required 
                    />
                    {errors.Area && <div className="error">{errors.Area}</div>}
                </div>
                <div className="input-group">
                    <label>Bedrooms:</label>
                    <input 
                        type="number" 
                        name="Bedrooms" 
                        value={propertyInput.Bedrooms} 
                        onChange={handleInputChange} 
                        required 
                    />
                    {errors.Bedrooms && <div className="error">{errors.Bedrooms}</div>}
                </div>
                <div className="input-group">
                    <label>Bathrooms:</label>
                    <input 
                        type="number" 
                        name="Bathrooms" 
                        value={propertyInput.Bathrooms} 
                        onChange={handleInputChange} 
                        required 
                    />
                    {errors.Bathrooms && <div className="error">{errors.Bathrooms}</div>}
                </div>
                <div className="input-group">
                    <label>Location:</label>
                    <div className="radio-input">
                        {['Downtown', 'Suburban', 'Rural'].map((location) => (
                            <label key={location}>
                                <input 
                                    type="radio" 
                                    id={location} 
                                    name="Location" 
                                    value={location} 
                                    checked={propertyInput.Location === location}
                                    onChange={handleInputChange}
                                    required
                                />
                                {location}
                            </label>
                        ))}
                        <span className="selection"></span>
                    </div>

                </div>

                <div className="input-group">
                    <label>Age of Property:</label>
                    <input 
                        type="number" 
                        name="Age of Property" 
                        value={propertyInput["Age of Property"]} 
                        onChange={handleInputChange} 
                        required 
                    />
                    {errors['Age of Property'] && <div className="error">{errors['Age of Property']}</div>}
                </div>
                <button className="btn-12" type="submit">
                    <span>Predict Price</span>
                </button>
            </form>

            {predictedPrice !== null && (
                <div className="form-container-predicted-price">
                    <h3 className="title">Predicted Price: ${predictedPrice.toLocaleString()}</h3>
                </div>
            )}
        </div>
    );
};

export default PropertyInputForm;
