// validation.js

export const errorMessages = {
    Area: "The area should be at least 100!",
    Bedrooms: "Unless you're running a hotel, that's too many bedrooms!",
    Bathrooms: "That's more bathrooms than a shopping mall! Let's keep it realistic.",
    "Age of Property": "Even the pyramids aren't that old! Try a younger age.",
    Location: "Please choose only one location!"
};

export const maxValues = {
    Area: 10000,
    Bedrooms: 10,
    Bathrooms: 8,
    "Age of Property": 200
};

export const minValues = {
    Area: 100, // Min area value
    Bedrooms: 1, // Min bedroom value (realistic)
    Bathrooms: 1, // Min bathroom value (realistic)
    "Age of Property": 1 // Min age value (realistic)
};

export const validateInput = (name, value) => {
    // Handle min value checks
    if (value < minValues[name]) {
        return errorMessages[name];
    }
    // Handle max value checks
    if (value > maxValues[name]) {
        return errorMessages[name];
    }
    return ""; // No error
};

export const validateLocation = (location) => {
    // Ensure only one location is selected
    if (Array.isArray(location) && location.length !== 1) {
        return errorMessages.Location;
    }
    return ""; // No error
};
