// utils/validation.js
export const validatePropertyInput = (propertyInput) => {
    const errors = {};

    if (!propertyInput.Area || propertyInput.Area <= 0) {
        errors.Area = "Area must be a positive number";
    }

    if (!propertyInput.Bedrooms || propertyInput.Bedrooms <= 0 || !Number.isInteger(propertyInput.Bedrooms)) {
        errors.Bedrooms = "Bedrooms must be a positive integer";
    }

    if (!propertyInput.Bathrooms || propertyInput.Bathrooms <= 0) {
        errors.Bathrooms = "Bathrooms must be a positive number";
    }

    if (!propertyInput.Location) {
        errors.Location = "Please select a location";
    }

    if (!propertyInput["Age of Property"] || propertyInput["Age of Property"] < 0) {
        errors["Age of Property"] = "Age must be a positive number";
    }

    return {
        isValid: Object.keys(errors).length === 0,
        errors
    };
};
