// components/PropertyInputForm.js
import React from 'react';

const PropertyInputForm = ({ propertyInput, handleInputChange, handlePredictPrice, predictedPrice }) => {
    const handleSubmit = (e) => {
        e.preventDefault();
        handlePredictPrice();
    };

    return (
        <>
            <form id="property-input-form" name="property-input-form-name" onSubmit={handleSubmit}> 
                <label>
                    Area:
                    <input type="number" name="Area" value={propertyInput.Area} onChange={handleInputChange} required />
                </label>
                <label>
                    Bedrooms:
                    <input type="number" name="Bedrooms" value={propertyInput.Bedrooms} onChange={handleInputChange} required />
                </label>
                <label>
                    Bathrooms:
                    <input type="number" name="Bathrooms" value={propertyInput.Bathrooms} onChange={handleInputChange} required />
                </label>
                <label>
                    Location:
                    <div className="radio-input">
                        {['Downtown', 'Suburban', 'Rural'].map((location) => (
                            <label key={location}>
                                <input 
                                    type="radio" 
                                    id={location} 
                                    name="Location" 
                                    value={location} 
                                    checked={propertyInput.Location === location}
                                    onChange={handleInputChange}
                                    required
                                />
                                <span>{location}</span>
                            </label>
                        ))}
                        <span className="selection"></span>
                    </div>
                </label>
                <label>
                    Age of Property:
                    <input type="number" name="Age of Property" value={propertyInput["Age of Property"]} onChange={handleInputChange} required />
                </label>
                <button type="submit">Predict Price</button>
            </form>
            {predictedPrice !== null && (
                <div className="predicted-price">
                    <h3>Predicted Price: ${predictedPrice.toLocaleString()}</h3>
                </div>
            )}
        </>
    );
};

export default PropertyInputForm;
