export const errorMessages = {
    AreaMax: "Whoa there, cowboy! That's bigger than Texas! Try a smaller area.",
    AreaMin: "The area should be at least 100!",
    BedroomsMax: "Unless you're running a hotel, that's too many bedrooms!",
    BedroomsMin: "Where will you sleep? Please enter at least 1 bedroom.",
    BathroomsMax: "That's more bathrooms than a shopping mall! Let's keep it realistic.",
    BathroomsMin: "At least 1 bathroom is required.",
    AgeMax: "Even the pyramids aren't that old! Try a younger age.",
    AgeMin: "The property age can't be negative!"
};

export const minValues = {
    Area: 100,
    Bedrooms: 1,
    Bathrooms: 1,
    "Age of Property": 0
};

export const maxValues = {
    Area: 10000,
    Bedrooms: 10,
    Bathrooms: 8,
    "Age of Property": 200
};

export const validateInput = (name, value) => {
    if (value < minValues[name]) {
        return errorMessages[`${name}Min`];
    }
    if (value > maxValues[name]) {
        return errorMessages[`${name}Max`];
    }
    return "";
};
