const handleSubmit = (e) => {
    e.preventDefault();
    const newErrors = {};

    if (propertyInput.Area <= 101) {
        newErrors.Area = "Wow, that's tiny! Please enter a larger area. Are you looking for a cozy studio apartment?";
    } else if (propertyInput.Area > 5000 && propertyInput.Area <= 7000) {
        newErrors.Area = "Wow, that's spacious! Are you searching for a large family home? Please enter a more typical house size if not.";
    } else if (propertyInput.Area > 7000 && propertyInput.Area <= 10000) {
        newErrors.Area = "That's enormous! Are you looking for a mansion? Please enter a smaller size for typical homes.";
    } else if (propertyInput.Area > 10000) {
        newErrors.Area = "That's gigantic! This tool is for residential properties only. Please enter a smaller size.";
    }

    if (propertyInput.Bedrooms === 0) {
        newErrors.Bedrooms = "No bedrooms? Please enter at least one bedroom. Are you looking for a studio apartment?";
    } else if (propertyInput.Bedrooms < 0) {
        newErrors.Bedrooms = "Negative bedrooms don't exist! Please enter a positive number of bedrooms.";
    } else if (propertyInput.Bedrooms > 8) {
        newErrors.Bedrooms = "Wow, that's a lot of bedrooms! This tool is for residential properties only. Please enter 8 or fewer bedrooms.";
    }

    if (propertyInput.Bathrooms === 0) {
        newErrors.Bathrooms = "A house without bathrooms? Please enter at least one bathroom. Every home needs a bathroom!";
    } else if (propertyInput.Bathrooms < 0) {
        newErrors.Bathrooms = "Negative bathrooms aren't a thing! Please enter a positive number of bathrooms.";
    } else if (propertyInput.Bathrooms > 10) {
        newErrors.Bathrooms = "That's a lot of bathrooms! This tool is for residential properties only. Please enter 10 or fewer bathrooms.";
    }

    if (propertyInput["Age of Property"] < 0) {
        newErrors["Age of Property"] = "A house from the future? Please enter a positive number for the property's age.";
    } else if (propertyInput["Age of Property"] > 200) {
        newErrors["Age of Property"] = "Wow, that's ancient! Are you searching for a historic home? Please enter a lower age for more typical properties.";
    }

    if (!propertyInput.Location) {
        newErrors.Location = "We need to know where it is! Please enter a location for the property.";
    }

    setErrors(newErrors);

    if (Object.keys(newErrors).length === 0) {
        handlePredictPrice();
    }
};
