import React, { useState, useEffect } from 'react';
import './App.css';
import { trainNetwork, predictPrice } from './neuralNetwork';

const App = () => {
    const [predictedPrice, setPredictedPrice] = useState(null);
    const [propertyInput, setPropertyInput] = useState({
        Area: "2500",
        Bedrooms: "3",
        Bathrooms: "2",
        Location: "Suburban",
        "Age of Property": "15"
    });
    const [trainingComplete, setTrainingComplete] = useState(false);  // Track training status

    useEffect(() => {
        trainNetwork((stats) => {
            console.log(`Training: ${stats.iterations} iterations`);
        }, () => { // Add completion callback
            setTrainingComplete(true);
        });
    }, []);

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setPropertyInput(prevInput => ({ ...prevInput, [name]: value }));
    };

    const handlePredictClick = () => {
        if (trainingComplete) {
            const predictedPriceValue = predictPrice(propertyInput);
            setPredictedPrice(predictedPriceValue);
        } else {
            alert("Training not complete yet. Please wait.");
        }
    };

    return (
        <div className="App">
            <h1>Real Estate Price Predictor</h1>

            {/* Input form */}
            <label>
                Area:
                <input type="number" name="Area" value={propertyInput.Area} onChange={handleInputChange} />
            </label>
            <label>
                Bedrooms:
                <input type="number" name="Bedrooms" value={propertyInput.Bedrooms} onChange={handleInputChange} />
            </label>
            <label>
                Bathrooms:
                <input type="number" name="Bathrooms" value={propertyInput.Bathrooms} onChange={handleInputChange} />
            </label>
            <label>
                Location:
                <select name="Location" value={propertyInput.Location} onChange={handleInputChange}>
                    <option value="Downtown">Downtown</option>
                    <option value="Suburban">Suburban</option>
                    <option value="Rural">Rural</option>
                </select>
            </label>
            <label>
                Age of Property:
                <input type="number" name="Age of Property" value={propertyInput["Age of Property"]} onChange={handleInputChange} />
            </label>

            <button onClick={handlePredictClick} disabled={!trainingComplete}>Predict Price</button>

            {predictedPrice !== null ? (
                <p>Predicted Price: {predictedPrice.toFixed(2)}</p>
            ) : (
                <p>Enter property details and click 'Predict Price'</p>
            )}
        </div>
    );
};

export default App;
