// utils/validation.js

export const validateInputs = (PropertyInputForm) => {
    const errors = {};

    if (!PropertyInputForm.Area || PropertyInputForm.Area <= 0) {
        errors.Area = "Area must be a positive number.";
    }

    if (!PropertyInputForm.Bedrooms || PropertyInputForm.Bedrooms <= 0) {
        errors.Bedrooms = "Bedrooms must be a positive number.";
    }

    if (!PropertyInputForm.Bathrooms || PropertyInputForm.Bathrooms <= 0) {
        errors.Bathrooms = "Bathrooms must be a positive number.";
    }

    if (!PropertyInputForm.Location) {
        errors.Location = "Location is required.";
    }

    if (!PropertyInputForm["Age of Property"] || PropertyInputForm["Age of Property"] <= 0) {
        errors["Age of Property"] = "Age of Property must be a positive number.";
    }

    return errors;
};
