// utils/validation.js

export const validateInputs = (propertyInput) => {
    const errors = {};

    if (!propertyInput.Area || propertyInput.Area <= 0) {
        errors.Area = "Area must be a positive number.";
    }

    if (!propertyInput.Bedrooms || propertyInput.Bedrooms <= 0) {
        errors.Bedrooms = "Bedrooms must be a positive number.";
    }

    if (!propertyInput.Bathrooms || propertyInput.Bathrooms <= 0) {
        errors.Bathrooms = "Bathrooms must be a positive number.";
    }

    if (!propertyInput.Location) {
        errors.Location = "Location is required.";
    }

    if (!propertyInput["Age of Property"] || propertyInput["Age of Property"] <= 0) {
        errors["Age of Property"] = "Age of Property must be a positive number.";
    }

    return errors;
};
