// App.js
import React, { useState } from 'react';
import './App.css';
import PropertyInputForm from './components/PropertyInputForm';
import PredictedPrice from './components/PredictedPrice';
import PredictPriceButton from './components/PredictPriceButton';
import useRealEstatePrediction from './useRealEstatePrediction';

const App = () => {
    const {
        predictedPrice,
        propertyInput,
        handleInputChange,
        handlePredictPrice
    } = useRealEstatePrediction();

    const [isPricePredicted, setIsPricePredicted] = useState(false); // New state to track button click

    const handleButtonClick = () => {
        handlePredictPrice(); // Call the handlePredictPrice function
        setIsPricePredicted(true); // Update state to show price after button click
    };

    return (
        <div className="App">
            <h1>Real Estate Price Predictor</h1>
            <PropertyInputForm 
                propertyInput={propertyInput} 
                handleInputChange={handleInputChange} 
            />
            <PredictPriceButton handlePredictPrice={handleButtonClick} /> {/* Use the updated function */}

            {isPricePredicted && <PredictedPrice predictedPrice={predictedPrice} />} {/* Conditionally render PredictedPrice */}
        </div>
    );
};

export default App;
