// components/PropertyInputForm.js
import React, { useState } from 'react';
import { validateInputs } from '../utils/validation';

const PropertyInputForm = ({ propertyInput, handleInputChange }) => {
    const [errors, setErrors] = useState({});

    const handleSubmit = (event) => {
        event.preventDefault();

        const validationErrors = validateInputs(propertyInput);
        if (Object.keys(validationErrors).length > 0) {
            setErrors(validationErrors);
        } else {
            // Proceed with prediction if no errors
            setErrors({});
            handleInputChange(event);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Area:
                <input
                    type="number"
                    name="Area"
                    value={propertyInput.Area}
                    onChange={handleInputChange}
                />
                {errors.Area && <p className="error">{errors.Area}</p>}
            </label>

            <label>
                Bedrooms:
                <input
                    type="number"
                    name="Bedrooms"
                    value={propertyInput.Bedrooms}
                    onChange={handleInputChange}
                />
                {errors.Bedrooms && <p className="error">{errors.Bedrooms}</p>}
            </label>

            <label>
                Bathrooms:
                <input
                    type="number"
                    name="Bathrooms"
                    value={propertyInput.Bathrooms}
                    onChange={handleInputChange}
                />
                {errors.Bathrooms && <p className="error">{errors.Bathrooms}</p>}
            </label>

            <label>
                Location:
                <select
                    name="Location"
                    value={propertyInput.Location}
                    onChange={handleInputChange}
                >
                    <option value="none">Select Location</option>
                    <option value="Downtown">Downtown</option>
                    <option value="Suburban">Suburban</option>
                    <option value="Rural">Rural</option>
                </select>
                {errors.Location && <p className="error">{errors.Location}</p>}
            </label>

            <label>
                Age of Property:
                <input
                    type="number"
                    name="Age of Property"
                    value={propertyInput["Age of Property"]}
                    onChange={handleInputChange}
                />
                {errors["Age of Property"] && <p className="error">{errors["Age of Property"]}</p>}
            </label>

            <button type="submit">Submit</button>
        </form>
    );
};

export default PropertyInputForm;
