import { 
    minArea, maxArea, 
    minBedrooms, maxBedrooms, 
    minBathrooms, maxBathrooms, 
    minAge, maxAge 
} from '../neuralNetwork.js';

export const errorMessages = {
    Area: `Area must be between ${minArea} and ${maxArea}`,
    Bedrooms: `Bedrooms must be between ${minBedrooms} and ${maxBedrooms}`,
    Bathrooms: `Bathrooms must be between ${minBathrooms} and ${maxBathrooms}`,
    "Age of Property": `Age must be between ${minAge} and ${maxAge}`
};

export const validateInput = (name, value) => {
    const ranges = {
        Area: { min: 1, max: maxArea },
        Bedrooms: { min: 1, max: maxBedrooms },
        Bathrooms: { min: 1, max: maxBathrooms },
        "Age of Property": { min: 1, max: maxAge }
    };

    const { min, max } = ranges[name];
    if (value < min || value > max) {
        return errorMessages[name];
    }
    return "";
};
