import React, { useState } from 'react';
import '../styles/formStyles.css';
import { validateInput } from './formValidation';

const PropertyInputForm = ({ propertyInput, handleInputChange, handlePredictPrice, predictedPrice }) => {
    const [errors, setErrors] = useState({});

    const handleInputChangeWithValidation = (e) => {
        const { name, value } = e.target;
        const error = validateInput(name, value);
        setErrors({ ...errors, [name]: error });
        if (!error) {
            handleInputChange(e);
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (Object.values(errors).every(error => error === "")) {
            handlePredictPrice();
        }
    };

    return (
        <div className="form-container">
            <form className="form" onSubmit={handleSubmit}>
                <div className="input-group">
                    <label>Area:</label>
                    <input 
                        type="number" 
                        name="Area" 
                        value={propertyInput.Area} 
                        onChange={handleInputChangeWithValidation} 
                        required 
                    />
                    {errors.Area && <span className="error">{errors.Area}</span>}
                </div>
                <div className="input-group">
                    <label>Bedrooms:</label>
                    <input 
                        type="number" 
                        name="Bedrooms" 
                        value={propertyInput.Bedrooms} 
                        onChange={handleInputChangeWithValidation} 
                        required 
                    />
                    {errors.Bedrooms && <span className="error">{errors.Bedrooms}</span>}
                </div>
                <div className="input-group">
                    <label>Bathrooms:</label>
                    <input 
                        type="number" 
                        name="Bathrooms" 
                        value={propertyInput.Bathrooms} 
                        onChange={handleInputChangeWithValidation} 
                        required 
                    />
                    {errors.Bathrooms && <span className="error">{errors.Bathrooms}</span>}
                </div>
                <div className="input-group">
                    <label>Location:</label>
                    <div className="radio-input">
                        {['Downtown', 'Suburban', 'Rural'].map((location) => (
                            <label key={location}>
                                <input 
                                    type="radio" 
                                    id={location} 
                                    name="Location" 
                                    value={location} 
                                    checked={propertyInput.Location === location}
                                    onChange={handleInputChange}
                                    required
                                />
                                {location}
                            </label>
                        ))}
                        <span className="selection"></span>
                    </div>
                </div>
                <div className="input-group">
                    <label>Age of Property:</label>
                    <input 
                        type="number" 
                        name="Age of Property" 
                        value={propertyInput["Age of Property"]} 
                        onChange={handleInputChangeWithValidation} 
                        required 
                    />
                    {errors["Age of Property"] && <span className="error">{errors["Age of Property"]}</span>}
                </div>
                <button className="btn-12" type="submit" disabled={Object.values(errors).some(error => error !== "")}>
                    <span>Predict Price</span>
                </button>
            </form>

            {predictedPrice !== null && (
                <div className="form-container-predicted-price">
                    <h3 className="title">Predicted Price: ${predictedPrice.toLocaleString()}</h3>
                </div>
            )}
        </div>
    );
};

export default PropertyInputForm;
