import { maxArea, maxBedrooms, maxBathrooms, maxAge } from '../neuralNetwork';

export const errorMessages = {
    AreaMax: "Whoa there, cowboy! That's bigger than Texas! Try a smaller area.",
    AreaMin: "Area should be at least 100!",
    Bedrooms: "Unless you're running a hotel, that's too many bedrooms!",
    Bathrooms: "That's more bathrooms than a shopping mall! Let's keep it realistic.",
    "Age of Property": "Even the pyramids aren't that old! Try a younger age."
};

export const maxValues = {
    Area: maxArea,
    Bedrooms: maxBedrooms,
    Bathrooms: maxBathrooms,
    "Age of Property": maxAge
};

export const validateInput = (name, value, previousFieldFilled = true) => {
    if (name === "Area") {
        if (previousFieldFilled) {  // Ensure user has navigated to the next field
            if (value < 100) {
                return errorMessages.AreaMin;
            }
            if (value > maxValues.Area) {
                return errorMessages.AreaMax;
            }
        }
    } else {
        if (value > maxValues[name]) {
            return errorMessages[name];
        }
    }

    return "";
};
