import { validateInput, validateLocation, errorMessages } from '../utils/validation';

document.getElementById('realEstateForm').addEventListener('submit', function(event) {
    event.preventDefault(); // Prevent form submission for validation

    const area = parseInt(document.getElementById('area').value);
    const bedrooms = parseInt(document.getElementById('bedrooms').value);
    const bathrooms = parseInt(document.getElementById('bathrooms').value);
    const location = [document.getElementById('location').value];
    const age = parseInt(document.getElementById('age').value);

    // Clear previous error messages
    clearErrorMessages();

    // Validate each field
    let areaError = validateInput("Area", area);
    let bedroomsError = validateInput("Bedrooms", bedrooms);
    let bathroomsError = validateInput("Bathrooms", bathrooms);
    let locationError = validateLocation(location);
    let ageError = validateInput("Age of Property", age);

    // Display error messages
    if (areaError) {
        document.getElementById('areaError').innerText = areaError;
    }
    if (bedroomsError) {
        document.getElementById('bedroomsError').innerText = bedroomsError;
    }
    if (bathroomsError) {
        document.getElementById('bathroomsError').innerText = bathroomsError;
    }
    if (locationError) {
        document.getElementById('locationError').innerText = locationError;
    }
    if (ageError) {
        document.getElementById('ageError').innerText = ageError;
    }

    // If no errors, submit the form (this can be replaced with actual form submission logic)
    if (!areaError && !bedroomsError && !bathroomsError && !locationError && !ageError) {
        console.log('Form submitted!');
    }
});

// Function to clear error messages
function clearErrorMessages() {
    document.getElementById('areaError').innerText = '';
    document.getElementById('bedroomsError').innerText = '';
    document.getElementById('bathroomsError').innerText = '';
    document.getElementById('locationError').innerText = '';
    document.getElementById('ageError').innerText = '';
}
